mapper.insert:
INSERT 
       INTO OAUTH_TOKENS 
(
       PROVIDER
     , PROVIDER_USER_ID
     , ACCESS_TOKEN
     , REFRESH_TOKEN
     , SCOPE
     , TOKEN_TYPE
     , EXPIRES_AT
     , CREATED_AT
     , UPDATED_AT
) 
VALUES (
       #{provider}
     , #{providerUserId}
     , #{accessToken}
     , #{refreshToken}
     , #{scope}
     , #{tokenType}
     , #{expiresAt}
     , NOW()
     , NOW()
)

mapper.update:
UPDATE OAUTH_TOKENS
   SET 
       ACCESS_TOKEN     = #{accessToken}
     , REFRESH_TOKEN    = #{refreshToken}
     , SCOPE            = #{scope}
     , TOKEN_TYPE       = #{tokenType}
     , EXPIRES_AT       = #{expiresAt}
     , UPDATED_AT       = NOW()
 WHERE PROVIDER_USER_ID = #{providerUserId}

mapper.insert:
INSERT 
       INTO USR_INFO 
(
       NICK_NM
     , EMAIL
     , PROV
     , USR_ROLE
     , REG_DT
     , MDFCN_DT
) 
VALUES (
       #{nickNm}
     , #{email}
     , #{prov}
     , #{usrRole}
     , NOW()
     , NOW()
)
